mapper.select:
SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.insert:
INSERT INTO TBL_MEMBER(ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME,
                        MEMBER_PHONE, MEMBER_NICKNAME, MEMBER_ZIPCODE, MEMBER_ADDRESS,
                        MEMBER_ADDRESS_DETAIL, MEMBER_IMAGE, MEMBER_SMSCHECK, MEMBER_EMAILCHECK,
                        MEMBER_DATE, MEMBER_BUSINESS_NUMBER, MEMBER_AUTH, MEMBER_PROVIDER,
                        MEMBER_BUSINESS_NAME, MEMBER_BANK, MEMBER_BANK_ACCOUNT)
VALUES(
SEQ_MEMBER.NEXTVAL,
#{memberEmail},
#{memberPassword}
NULL,
#{memberName},
#{memberPhone},
#{memberNickname}
DEFAULT,
#{memberZipcode}
NULL,
#{memberAddress}
NULL,
#{memberAddressDetail}
NULL,
#{memberImage}
DEFAULT,
#{memberSmsCheck}
DEFAULT,
#{memberEmailCheck}
DEFAULT,
#{memberDate}
DEFAULT,
#{memberBusinessNumber}
NULL,
#{memberAuth}
DEFAULT,
#{memberProvider}
DEFAULT
#{memberBusinessName}
NULL
#{memberBank}
NULL
#{memberBankAccount}
NULL
)

mapper.select:
SELECT COUNT(*)
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}
