mapper.select:
SELECT ID, PRODUCT_ID, MEMBER_ID, DELIVERY_ID, ORDER_NUMBER, ORDER_DATE, ORDER_ADDRESS, PRODUCT_COUNT
FROM TBL_ORDER
WHERE ID = #{memberId}

mapper.update:
UPDATE TBL_ORDER
SET PRODCUT_ID = #{productId}, MEMBER_ID = #{memberId}, DELIVERY_ID = #{deliveryId}, ORDER_NUMBER = #{orderNumber}, ORDER_DATE = #{orderDate}, ORDER_ADDRESS = #{orderAddress}, PRODUCT_COUNT = #{productCount}
WHERE ID = #{id}
